import React, {useState} from "react";

import "./ExpenseForm.css";

function ExpenseForm() {
    // const [enteredTitle, setEnteredTitle] = useState('');
    // const [enteredAmount, setEnteredAmount] = useState('');
    // const [enteredDate, setEnteredDate] = useState(''); //onChange always returns a string

    const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
        enteredDate: ''
    })

    function titleChangeHandler(event) { //event object describes the event that occured
        // setEnteredTitle(event.target.value);
        setUserInput({
            ...userInput, //adds all of the current key value pairs
            enteredTitle: event.target.value //overwrites the enteredTitle key value pair
        });
    }

    function amountChangeHandler(event) {
        //setEnteredAmount(event.target.value);
        setUserInput({
            enteredTitle: userInput.enteredTitle,
            enteredAmount: event.target.value,
            enteredDate: userInput.enteredDate
        });
    }

    function dateChangeHandler(event) {
        //setEnteredDate(event.target.value);
        setUserInput({
            enteredTitle: userInput.enteredTitle,
            enteredAmount: userInput.enteredAmount,
            enteredDate: event.target.value
        });
    }

    return <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
            </div>
        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
    </form>;
}

export default ExpenseForm;
